// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "../interfaces/IACLRegistry.sol";

/**
 * @dev Contract module that allows children to implement role-based access
 * control mechanisms. This is a lightweight version that doesn't allow enumerating role
 * members except through off-chain means by accessing the contract event logs. Some
 * applications may benefit from on-chain enumerability, for those cases see
 * {AccessControlEnumerable}.
 *
 * Roles are referred to by their `bytes32` identifier. These should be exposed
 * in the external API and be unique. The best way to achieve this is by
 * using `public constant` hash digests:
 *
 * ```
 * bytes32 public constant MY_ROLE = keccak256("MY_ROLE");
 * ```
 *
 * Roles can be used to represent a set of permissions. To restrict access to a
 * function call, use {hasRole}:
 *
 * ```
 * function foo() public {
 *     require(hasRole(MY_ROLE, msg.sender));
 *     ...
 * }
 * ```
 *
 * Roles can be granted and revoked dynamically via the {grantRole} and
 * {revokeRole} functions. Each role has an associated admin role, and only
 * accounts that have a role's admin role can call {grantRole} and {revokeRole}.
 *
 * By default, the admin role for all roles is `DEFAULT_ADMIN_ROLE`, which means
 * that only accounts with this role will be able to grant or revoke other
 * roles. More complex role relationships can be created by using
 * {_setRoleAdmin}.
 *
 * WARNING: The `DEFAULT_ADMIN_ROLE` is also its own admin: it has permission to
 * grant and revoke this role. Extra precautions should be taken to secure
 * accounts that have been granted it.
 */
contract ACLRegistry is IACLRegistry {
  /* ========== STATE VARIABLES ========== */

  struct RoleData {
    mapping(address => bool) members;
    bytes32 adminRole;
  }

  mapping(bytes32 => RoleData) private _roles;
  mapping(bytes32 => address) private _permissions;

  bytes32 public constant DEFAULT_ADMIN_ROLE = 0x00;

  /* ========== CONSTRUCTOR ========== */

  constructor() {
    _roles[DEFAULT_ADMIN_ROLE].members[msg.sender] = true;
  }

  /* ========== VIEW FUNCTIONS ========== */

  /**
   * @dev Returns `true` if `account` has been granted `role`.
   */
  function hasRole(bytes32 role, address account) public view override returns (bool) {
    return _roles[role].members[account];
  }

  /**
   * @dev Returns `true` if `account` has been granted `permission`.
   */
  function hasPermission(bytes32 permission, address account) public view override returns (bool) {
    return _permissions[permission] == account;
  }

  /**
   * @dev Returns the admin role that controls `role`. See {grantRole} and
   * {revokeRole}.
   *
   * To change a role's admin, use {_setRoleAdmin}.
   */
  function getRoleAdmin(bytes32 role) public view override returns (bytes32) {
    return _roles[role].adminRole;
  }

  function requireRole(bytes32 role, address account) public view override {
    require(hasRole(role, account), "you dont have the right role");
  }

  function requirePermission(bytes32 permission, address account) public view override {
    require(hasPermission(permission, account), "you dont have the right permissions");
  }

  function isRoleAdmin(bytes32 role, address account) public view override {
    require(hasRole(getRoleAdmin(role), account), "you have to be role admin");
  }

  function requireApprovedContractOrEOA(address account) public view override {
    require(hasRole(keccak256("ApprovedContract"), account) || account == tx.origin, "Access denied for caller");
  }

  /* ========== MUTATIVE FUNCTIONS ========== */

  /**
   * @dev Grants `role` to `account`.
   *
   * If `account` had not been already granted `role`, emits a {RoleGranted}
   * event.
   *
   * Requirements:
   *
   * - the caller must have ``role``'s admin role.
   */
  function grantRole(bytes32 role, address account) public override onlyRole(getRoleAdmin(role)) {
    _grantRole(role, account);
  }

  /**
   * @dev Revokes `role` from `account`.
   *
   * If `account` had been granted `role`, emits a {RoleRevoked} event.
   *
   * Requirements:
   *
   * - the caller must have ``role``'s admin role.
   */
  function revokeRole(bytes32 role, address account) public override onlyRole(getRoleAdmin(role)) {
    _revokeRole(role, account);
  }

  /**
   * @dev Revokes `role` from the calling account.
   *
   * Roles are often managed via {grantRole} and {revokeRole}: this function's
   * purpose is to provide a mechanism for accounts to lose their privileges
   * if they are compromised (such as when a trusted device is misplaced).
   *
   * If the calling account had been granted `role`, emits a {RoleRevoked}
   * event.
   *
   * Requirements:
   *
   * - the caller must be `account`.
   */
  function renounceRole(bytes32 role, address account) public override {
    require(account == msg.sender || hasRole(getRoleAdmin(role), msg.sender), "you cant renounce this role");

    _revokeRole(role, account);
  }

  function setRoleAdmin(bytes32 role, bytes32 adminRole) public override {
    require(
      hasRole(getRoleAdmin(role), msg.sender) || hasRole(DEFAULT_ADMIN_ROLE, msg.sender),
      "can only renounce roles for self"
    );

    _setRoleAdmin(role, adminRole);
  }

  function grantPermission(bytes32 permission, address account) public override {
    require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "only for admin");
    _permissions[permission] = account;
  }

  function revokePermission(bytes32 permission) public override {
    require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "only for admin");
    delete _permissions[permission];
  }

  /* ========== RESTRICTED FUNCTIONS ========== */

  /**
   * @dev Revert with a standard message if `account` is missing `role`.
   *
   * The format of the revert reason is given by the following regular expression:
   *
   *  /^AccessControl: account (0x[0-9a-f]{40}) is missing role (0x[0-9a-f]{64})$/
   */
  function _requireRole(bytes32 role, address account) internal view {
    require(hasRole(role, account), "you dont have the required role");
  }

  /**
   * @dev Grants `role` to `account`.
   *
   * If `account` had not been already granted `role`, emits a {RoleGranted}
   * event. Note that unlike {grantRole}, this function doesn't perform any
   * checks on the calling account.
   *
   * [WARNING]
   * ====
   * This function should only be called from the constructor when setting
   * up the initial roles for the system.
   *
   * Using this function in any other way is effectively circumventing the admin
   * system imposed by {AccessControl}.
   * ====
   */
  function _setupRole(bytes32 role, address account) internal {
    _grantRole(role, account);
  }

  /**
   * @dev Sets `adminRole` as ``role``'s admin role.
   *
   * Emits a {RoleAdminChanged} event.
   */
  function _setRoleAdmin(bytes32 role, bytes32 adminRole) internal {
    bytes32 previousAdminRole = getRoleAdmin(role);
    _roles[role].adminRole = adminRole;
    emit RoleAdminChanged(role, previousAdminRole, adminRole);
  }

  function _grantRole(bytes32 role, address account) private {
    if (!hasRole(role, account)) {
      _roles[role].members[account] = true;
      emit RoleGranted(role, account, msg.sender);
    }
  }

  function _revokeRole(bytes32 role, address account) private {
    if (hasRole(role, account)) {
      _roles[role].members[account] = false;
      emit RoleRevoked(role, account, msg.sender);
    }
  }

  /* ========== MODIFIER ========== */

  /**
   * @dev Modifier that checks that an account has a specific role. Reverts
   * with a standardized message including the required role.
   *
   * The format of the revert reason is given by the following regular expression:
   *
   *  /^AccessControl: account (0x[0-9a-f]{40}) is missing role (0x[0-9a-f]{64})$/
   *
   * _Available since v4.1._
   */
  modifier onlyRole(bytes32 role) {
    _requireRole(role, msg.sender);
    _;
  }
}

contracts/core/utils/KeeperIncentive.sol.content:
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../interfaces/IACLRegistry.sol";
import "../interfaces/IContractRegistry.sol";
import "../interfaces/IStaking.sol";

contract KeeperIncentive {
  using SafeERC20 for IERC20;

  struct Incentive {
    uint256 reward; //pop reward for calling the function
    bool enabled;
    bool openToEveryone; //can everyone call the function to get the reward or only approved?
  }

  /* ========== STATE VARIABLES ========== */

  IContractRegistry public contractRegistry;

  uint256 public incentiveBudget;
  mapping(bytes32 => Incentive[]) public incentives;
  mapping(bytes32 => address) public controllerContracts;
  uint256 public burnRate;
  address internal immutable burnAddress =
    0x00000000219ab540356cBB839Cbe05303d7705Fa; //ETH2.0 Staking Contract
  uint256 public requiredKeeperStake;

  /* ========== EVENTS ========== */

  event IncentiveCreated(
    bytes32 contractName,
    uint256 reward,
    bool openToEveryone
  );
  event IncentiveChanged(
    bytes32 contractName,
    uint256 oldReward,
    uint256 newReward,
    bool oldOpenToEveryone,
    bool newOpenToEveryone
  );
  event IncentiveFunded(uint256 amount);
  event ApprovalToggled(bytes32 contractName, bool openToEveryone);
  event IncentiveToggled(bytes32 contractName, bool enabled);
  event ControllerContractAdded(bytes32 contractName, address contractAddress);
  event Burned(uint256 amount);
  event BurnRateChanged(uint256 oldRate, uint256 newRate);
  event RequiredKeeperStakeChanged(
    uint256 oldRequirement,
    uint256 newRequirement
  );

  /* ========== CONSTRUCTOR ========== */

  constructor(
    IContractRegistry _contractRegistry,
    uint256 _burnRate,
    uint256 _requiredKeeperStake
  ) public {
    contractRegistry = _contractRegistry;
    burnRate = _burnRate; //25e16
    requiredKeeperStake = _requiredKeeperStake; // 2000 ether
  }

  /* ==========  MUTATIVE FUNCTIONS  ========== */

  function handleKeeperIncentive(
    bytes32 _contractName,
    uint8 _i,
    address _keeper
  ) external {
    require(
      msg.sender == controllerContracts[_contractName],
      "Can only be called by the controlling contract"
    );

    Incentive memory incentive = incentives[_contractName][_i];

    if (!incentive.openToEveryone) {
      IACLRegistry(contractRegistry.getContract(keccak256("ACLRegistry")))
        .requireRole(keccak256("Keeper"), _keeper);
      require(
        IStaking(contractRegistry.getContract(keccak256("Staking"))).balanceOf(
          _keeper
        ) >= requiredKeeperStake,
        "not enough pop at stake"
      );
    }
    if (incentive.enabled && incentive.reward <= incentiveBudget) {
      incentiveBudget = incentiveBudget - incentive.reward;
      uint256 amountToBurn = (incentive.reward * burnRate) / 1e18;
      uint256 incentivePayout = incentive.reward - amountToBurn;
      IERC20(contractRegistry.getContract(keccak256("POP"))).safeTransfer(
        _keeper,
        incentivePayout
      );
      _burn(amountToBurn);
    }
  }

  /* ========== SETTER ========== */

  /**
   * @notice Create Incentives for keeper to call a function
   * @param _contractName Name of contract that uses ParticipationRewards in bytes32
   * @param _reward The amount in POP the Keeper receives for calling the function
   * @param _enabled Is this Incentive currently enabled?
   * @param _openToEveryone Can anyone call the function for rewards or only keeper?
   * @dev This function is only for creating unique incentives for future contracts
   * @dev Multiple functions can use the same incentive which can than be updated with one governance vote
   */
  function createIncentive(
    bytes32 _contractName,
    uint256 _reward,
    bool _enabled,
    bool _openToEveryone
  ) public {
    IACLRegistry(contractRegistry.getContract(keccak256("ACLRegistry")))
      .requireRole(keccak256("DAO"), msg.sender);
    incentives[_contractName].push(
      Incentive({
        reward: _reward,
        enabled: _enabled,
        openToEveryone: _openToEveryone
      })
    );
    emit IncentiveCreated(_contractName, _reward, _openToEveryone);
  }

  /* ========== RESTRICTED FUNCTIONS ========== */

  function updateIncentive(
    bytes32 _contractName,
    uint8 _i,
    uint256 _reward,
    bool _enabled,
    bool _openToEveryone
  ) external {
    IACLRegistry(contractRegistry.getContract(keccak256("ACLRegistry")))
      .requireRole(keccak256("DAO"), msg.sender);
    Incentive storage incentive = incentives[_contractName][_i];
    uint256 oldReward = incentive.reward;
    bool oldOpenToEveryone = incentive.openToEveryone;
    incentive.reward = _reward;
    incentive.enabled = _enabled;
    incentive.openToEveryone = _openToEveryone;
    emit IncentiveChanged(
      _contractName,
      oldReward,
      _reward,
      oldOpenToEveryone,
      _openToEveryone
    );
  }

  function toggleApproval(bytes32 _contractName, uint8 _i) external {
    IACLRegistry(contractRegistry.getContract(keccak256("ACLRegistry")))
      .requireRole(keccak256("DAO"), msg.sender);
    Incentive storage incentive = incentives[_contractName][_i];
    incentive.openToEveryone = !incentive.openToEveryone;
    emit ApprovalToggled(_contractName, incentive.openToEveryone);
  }

  function toggleIncentive(bytes32 _contractName, uint8 _i) external {
    IACLRegistry(contractRegistry.getContract(keccak256("ACLRegistry")))
      .requireRole(keccak256("DAO"), msg.sender);
    Incentive storage incentive = incentives[_contractName][_i];
    incentive.enabled = !incentive.enabled;
    emit IncentiveToggled(_contractName, incentive.enabled);
  }

  function fundIncentive(uint256 _amount) external {
    IERC20(contractRegistry.getContract(keccak256("POP"))).safeTransferFrom(
      msg.sender,
      address(this),
      _amount
    );
    incentiveBudget = incentiveBudget + _amount;
    emit IncentiveFunded(_amount);
  }

  /**
   * @notice In order to allow a contract to use ParticipationReward they need to be added as a controller contract
   * @param _contractName the name of the controller contract in bytes32
   * @param contract_ the address of the controller contract
   * @dev all critical functions to init/open vaults and add shares to them can only be called by controller contracts
   */
  function addControllerContract(bytes32 _contractName, address contract_)
    external
  {
    IACLRegistry(contractRegistry.getContract(keccak256("ACLRegistry")))
      .requireRole(keccak256("DAO"), msg.sender);
    controllerContracts[_contractName] = contract_;
    emit ControllerContractAdded(_contractName, contract_);
  }

  /**
   * @notice Sets the current burn rate as a percentage of the incentive reward.
   * @param _burnRate Percentage in Mantissa. (1e14 = 1 Basis Point)
   */
  function updateBurnRate(uint256 _burnRate) external {
    IACLRegistry(contractRegistry.getContract(keccak256("ACLRegistry")))
      .requireRole(keccak256("DAO"), msg.sender);
    emit BurnRateChanged(burnRate, _burnRate);
    burnRate = _burnRate;
  }

  function _burn(uint256 _amount) internal {
    IERC20(contractRegistry.getContract(keccak256("POP"))).transfer(
      burnAddress,
      _amount
    );
    emit Burned(_amount);
  }

  /**
   * @notice Sets the required amount of POP a keeper needs to have staked to handle incentivized functions.
   * @param _amount Amount of POP a keeper needs to stake
   */
  function updateRequiredKeeperStake(uint256 _amount) external {
    IACLRegistry(contractRegistry.getContract(keccak256("ACLRegistry")))
      .requireRole(keccak256("DAO"), msg.sender);
    emit RequiredKeeperStakeChanged(requiredKeeperStake, _amount);
    requiredKeeperStake = _amount;
  }
}

contracts/core/dao/RewardsEscrow.sol.content:
// Docgen-SOLC: 0.8.0
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "../interfaces/IStaking.sol";
import "../interfaces/IRewardsEscrow.sol";

contract RewardsEscrow is IRewardsEscrow, ReentrancyGuard, Ownable {
  using SafeERC20 for IERC20;

  /* ========== STATE VARIABLES ========== */
  struct Escrow {
    uint256 start;
    uint256 end;
    uint256 balance;
    address account;
  }

  IERC20 public immutable POP;
  mapping(address => bool) public staking;
  mapping(bytes32 => Escrow) public escrows;
  mapping(address => bytes32[]) public escrowIdsByAddress;
  uint256 public escrowDuration = 365 days;
  uint256 internal nonce;

  /* ========== EVENTS ========== */
  event Locked(address account, uint256 amount);
  event RewardsClaimed(address account, uint256 amount);
  event EscrowDurationChanged(uint256 escrowDuration);
  event TokenAdded(address token, uint256 index);
  event AddStaking(address _contract);
  event RemoveStaking(address _contract);

  /* ========== CONSTRUCTOR ========== */

  constructor(IERC20 _pop) {
    POP = _pop;
  }

  /* ========== VIEWS ========== */

  /**
   * @notice Returns whether the escrow is claimable
   * @param _escrowId Bytes32 escrow ID
   */
  function isClaimable(bytes32 _escrowId) external view returns (bool) {
    return escrows[_escrowId].start != 0 && escrows[_escrowId].balance > 0;
  }

  /**
   * @notice Returns all escrowIdsByAddress which an account has/had claims in
   * @param _account address
   */
  function getEscrowIdsByUser(address _account) external view returns (bytes32[] memory) {
    return escrowIdsByAddress[_account];
  }

  /**
   * @notice Returns an array of Escrows
   * @param _escrowIds array of escrow ids
   * @dev there is no check to ensure that all escrows are owned by the same account. Make sure to account for this either by only sending ids for a specific account or by filtering the Escrows by account later on.
   */
  function getEscrows(bytes32[] calldata _escrowIds) external view returns (Escrow[] memory) {
    require(_escrowIds.length <= 20, "too many ids");

    Escrow[] memory selectedEscrows = new Escrow[](_escrowIds.length);
    for (uint256 i = 0; i < _escrowIds.length; i++) {
      selectedEscrows[i] = escrows[_escrowIds[i]];
    }
    return selectedEscrows;
  }

  /* ========== MUTATIVE FUNCTIONS ========== */

  /**
   * @notice Locks funds for escrow
   * @dev This creates a separate escrow structure which can later be iterated upon to unlock the escrowed funds
   */
  function lock(address _account, uint256 _amount) external override nonReentrant {
    require(staking[msg.sender], "unauthorized");
    require(_amount > 0, "amount must be greater than 0");
    require(POP.balanceOf(msg.sender) >= _amount, "insufficient balance");

    nonce++;
    uint256 start = block.timestamp;
    uint256 end = start + escrowDuration;
    bytes32 id = keccak256(abi.encodePacked(_account, _amount, start, nonce));

    escrows[id] = Escrow({start: start, end: end, balance: _amount, account: _account});
    escrowIdsByAddress[_account].push(id);

    POP.safeTransferFrom(msg.sender, address(this), _amount);

    emit Locked(_account, _amount);
  }

  /**
   * @notice Claim vested funds in escrow
   * @dev Uses the escrowId at the specified index of escrowIdsByAddress.
   * @dev This function is used when a user only wants to claim a specific escrowVault or if they decide the gas cost of claimRewards is too high for now.
   * @dev (lower cost but also lower reward)
   */
  function claimReward(bytes32 _escrowId) external nonReentrant {
    Escrow memory escrow = escrows[_escrowId];
    require(msg.sender == escrow.account, "unauthorized");
    uint256 reward = _claimReward(_escrowId);
    require(reward > 0, "no rewards");

    POP.safeTransfer(msg.sender, reward);

    emit RewardsClaimed(msg.sender, reward);
  }

  /**
   * @notice Claim rewards for multiple escrows
   * @dev Uses the vaultIds at the specified indices of escrowIdsByAddress.
   * @dev This function is used when a user wants to claim multiple escrowVaults at once (probably most of the time)
   * @dev The array of indices is limited to 20 as we want to prevent gas overflow of looping through too many vaults
   * TODO the upper bound of indices that can be used should be calculated with a simulation
   */
  function claimRewards(bytes32[] calldata _escrowIdsByAddress) external nonReentrant {
    require(_escrowIdsByAddress.length <= 20, "claiming too many escrows");
    uint256 total;

    for (uint256 i = 0; i < _escrowIdsByAddress.length; i++) {
      bytes32 _escrowId = _escrowIdsByAddress[i];
      Escrow memory escrow = escrows[_escrowId];
      require(msg.sender == escrow.account, "unauthorized");
      uint256 reward = _claimReward(_escrowId);
      total += reward;
    }
    require(total > 0, "no rewards");

    POP.safeTransfer(msg.sender, total);

    emit RewardsClaimed(msg.sender, total);
  }

  /* ========== RESTRICTED FUNCTIONS ========== */

  function updateEscrowDuration(uint256 _escrowDuration) external onlyOwner {
    escrowDuration = _escrowDuration;
    emit EscrowDurationChanged(_escrowDuration);
  }

  function addStakingContract(address _staking) external onlyOwner {
    staking[_staking] = true;
    emit AddStaking(_staking);
  }

  function removeStakingContract(address _staking) external onlyOwner {
    delete staking[_staking];
    emit RemoveStaking(_staking);
  }

  /**
   * @notice Underlying function to calculate the rewards that a user gets
   * @dev We dont want it to error when a vault is empty for the user as this would terminate the entire loop when used in claimRewards()
   */
  function _claimReward(bytes32 _escrowId) internal returns (uint256) {
    Escrow storage escrow = escrows[_escrowId];
    if (escrow.start <= block.timestamp) {
      uint256 claimable = _getClaimableAmount(escrow);
      escrow.balance -= claimable;
      return claimable;
    }
    return 0;
  }

  function _getClaimableAmount(Escrow memory _escrow) internal view returns (uint256) {
    if (_escrow.start == 0 || _escrow.end == 0) {
      return 0;
    }
    return
      Math.min((_escrow.balance * (block.timestamp - _escrow.start)) / (_escrow.end - _escrow.start), _escrow.balance);
  }
}

contracts/core/tokens/XPop.sol.content:
// SPDX-License-Identifier: GPL-3.0

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/draft-ERC20Permit.sol";

contract XPop is
  Ownable,
  ERC20("Popcorn.Network (Redeemable POP)", "xPOP"),
  ERC20Burnable,
  ERC20Permit("Popcorn.Network (Redeemable POP)")
{
  uint256 private immutable _mintCap;
  uint256 private _totalMinted;

  constructor(uint256 mintCap_) {
    require(mintCap_ > 0, "Mint cap is 0");
    _mintCap = mintCap_;
  }

  function mint(address to, uint256 amount) public onlyOwner {
    _mint(to, amount);
  }

  function totalMinted() public view returns (uint256) {
    return _totalMinted;
  }

  function mintCap() public view returns (uint256) {
    return _mintCap;
  }

  function _mint(address to, uint256 amount) internal override {
    require(_totalMinted + amount <= mintCap(), "Mint cap exceeded");
    _totalMinted += amount;
    super._mint(to, amount);
  }
}

